import React, { Component } from 'react';
import './App.css';
import Floor1 from './Floor1';
import Floor2 from './Floor2';
import Floor3 from './Floor3';
import PostData from '../data/content-by-sport.json'


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            floor2Img: []
        }

    }

    componentDidMount() {
        fetch('https://dog.ceo/api/breeds/image/random/5')
        .then(res => {
            return res.json()})
            .then((data) => {
                        this.setState({ floor2Img: data.message })
                    })
                }


    render(){
        this.setState.floor2Img=PostData.map((article) => {
            return article.image.media.resource;
        })
        return (
            <div className="App">
                <Floor1 />
                <Floor2 url={this.state.floor2Img}/>
                <Floor3 />
            </div>
        );
    }
}

export default App;

