import React, { Component } from 'react';
import './App.css';
import Floor1 from './Floor1';
import Floor2 from './Floor2';
import Floor3 from './Floor3';
import DataFloor2 from './data/content-by-sport.json';
import DataFloor3 from './data/sport-categories.json'


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            floor2Articles: [],
            floor3Categories: []
        }

    }

    componentDidMount() {
        this.setState({ floor2Articles: DataFloor2.data.articles.map((article) => {
            return article;
        })});
    }

    componentDidMount2() {
        this.setState({ floor3Categories: DataFloor2.data.articles.map((categorie) => {
            return categorie;
        })});

    }

    render(){
        return (
            <div className="App">
                <Floor1 />
                <Floor2 articles={this.state.floor2Articles}/>
                <Floor3 categories={this.state.floor2Articles}/>
            </div>
        );
    }
}

export default App;

